declare valores_antigos varchar(1000);
set valores_alterados = concat(new.Empregados_ID,"/",new.Primeiro_Nome,"/",new.Ultimo_Nome,"/",new.EMail,"/",new.Telefone,"/",new.Cargo_ID,"/",new.Salario,"/",new.Depto_ID,"/",new.Gerente_ID);
